import React, { useState } from "react";

function StepSix({ nextStep, prevStep, setUserData, userData }) {
  const [heightFeet, setHeightFeet] = useState(userData.heightFeet || "");
  const [heightInches, setHeightInches] = useState(userData.heightInches || "");
  const [currentWeight, setCurrentWeight] = useState(userData.currentWeight || "");
  const [goalWeight, setGoalWeight] = useState(userData.goalWeight || "");

  const handleNext = () => {
    if (!heightFeet.trim() || !heightInches.trim()) {
      alert("Please enter your full height.");
      return;
    }
    if (!currentWeight.trim()) {
      alert("Please enter your current weight.");
      return;
    }
    if (!goalWeight.trim()) {
      alert("Please enter your goal weight.");
      return;
    }
    setUserData((prev) => ({
      ...prev,
      heightFeet,
      heightInches,
      currentWeight,
      goalWeight,
    }));
    nextStep();
  };

  return (
    <div className="step">
      <h2>How tall are you?</h2>
      <p>Please provide your height and weight to calculate your goals.</p>
      <input
        type="number"
        placeholder="Height (feet)"
        className="input-field"
        value={heightFeet}
        onChange={(e) => setHeightFeet(e.target.value)}
      />
      <input
        type="number"
        placeholder="Height (inches)"
        className="input-field"
        value={heightInches}
        onChange={(e) => setHeightInches(e.target.value)}
      />
      <input
        type="number"
        placeholder="Current Weight (lbs)"
        className="input-field"
        value={currentWeight}
        onChange={(e) => setCurrentWeight(e.target.value)}
      />
      <input
        type="number"
        placeholder="Goal Weight (lbs)"
        className="input-field"
        value={goalWeight}
        onChange={(e) => setGoalWeight(e.target.value)}
      />
      <div className="button-group">
        <button className="btn-secondary" onClick={prevStep}>
          Back
        </button>
        <button className="btn-primary" onClick={handleNext}>
          Next
        </button>
      </div>
    </div>
  );
}

export default StepSix;
