import React, { useState } from "react";

function StepThree({ nextStep, prevStep, setUserData, userData }) {
  const [weeklyGoal, setWeeklyGoal] = useState(userData.weeklyGoal || "");

  const handleNext = () => {
    if (weeklyGoal.trim() === "") {
      alert("Please select a weekly goal.");
      return;
    }
    setUserData((prev) => ({ ...prev, weeklyGoal }));
    nextStep();
  };

  return (
    <div className="step">
      <h2>What is your weekly goal?</h2>
      <p>Letâ€™s break down your overall health goal into a weekly one you can maintain.</p>
      <div className="radio-group">
        <label>
          <input
            type="radio"
            name="weeklyGoal"
            value="Lose 0.5 pounds per week"
            checked={weeklyGoal === "Lose 0.5 pounds per week"}
            onChange={(e) => setWeeklyGoal(e.target.value)}
          />{" "}
          Lose 0.5 pounds per week
        </label>
        <br />
        <label>
          <input
            type="radio"
            name="weeklyGoal"
            value="Lose 1 pound per week (Recommended)"
            checked={weeklyGoal === "Lose 1 pound per week (Recommended)"}
            onChange={(e) => setWeeklyGoal(e.target.value)}
          />{" "}
          Lose 1 pound per week (Recommended)
        </label>
        <br />
        <label>
          <input
            type="radio"
            name="weeklyGoal"
            value="Lose 1.5 pounds per week"
            checked={weeklyGoal === "Lose 1.5 pounds per week"}
            onChange={(e) => setWeeklyGoal(e.target.value)}
          />{" "}
          Lose 1.5 pounds per week
        </label>
        <br />
        <label>
          <input
            type="radio"
            name="weeklyGoal"
            value="Lose 2 pounds per week"
            checked={weeklyGoal === "Lose 2 pounds per week"}
            onChange={(e) => setWeeklyGoal(e.target.value)}
          />{" "}
          Lose 2 pounds per week
        </label>
      </div>
      <div className="button-group">
        <button className="btn-secondary" onClick={prevStep}>
          Back
        </button>
        <button className="btn-primary" onClick={handleNext}>
          Next
        </button>
      </div>
    </div>
  );
}

export default StepThree;
