import React, { useState } from "react";

function StepTwo({ nextStep, prevStep, setUserData, userData }) {
  const [selectedGoals, setSelectedGoals] = useState(userData.goals || []);

  const toggleGoal = (goal) => {
    setSelectedGoals((prev) =>
      prev.includes(goal) ? prev.filter((g) => g !== goal) : [...prev, goal]
    );
  };

  const handleNext = () => {
    if (selectedGoals.length === 0) {
      alert("Please select at least one goal.");
      return;
    }
    setUserData((prev) => ({ ...prev, goals: selectedGoals }));
    nextStep();
  };

  return (
    <div className="step">
      <h2>Thanks! Now for your goals.</h2>
      <p>Select up to 3 goals that are important to you, including one weight goal.</p>
      <div className="checkbox-group">
        <label>
          <input
            type="checkbox"
            checked={selectedGoals.includes("Lose Weight")}
            onChange={() => toggleGoal("Lose Weight")}
          />{" "}
          Lose Weight
        </label>
        <br />
        <label>
          <input
            type="checkbox"
            checked={selectedGoals.includes("Gain Muscle")}
            onChange={() => toggleGoal("Gain Muscle")}
          />{" "}
          Gain Muscle
        </label>
        <br />
        <label>
          <input
            type="checkbox"
            checked={selectedGoals.includes("Maintain Weight")}
            onChange={() => toggleGoal("Maintain Weight")}
          />{" "}
          Maintain Weight
        </label>
        <br />
        <label>
          <input
            type="checkbox"
            checked={selectedGoals.includes("Modify My Diet")}
            onChange={() => toggleGoal("Modify My Diet")}
          />{" "}
          Modify My Diet
        </label>
        <br />
        <label>
          <input
            type="checkbox"
            checked={selectedGoals.includes("Manage Stress")}
            onChange={() => toggleGoal("Manage Stress")}
          />{" "}
          Manage Stress
        </label>
      </div>
      <div className="button-group">
        <button className="btn-secondary" onClick={prevStep}>
          Back
        </button>
        <button className="btn-primary" onClick={handleNext}>
          Next
        </button>
      </div>
    </div>
  );
}

export default StepTwo;
