import React, { useState } from "react";

function StepFour({ nextStep, prevStep, setUserData, userData }) {
  const [activityLevel, setActivityLevel] = useState(userData.activityLevel || "");

  const handleNext = () => {
    if (activityLevel.trim() === "") {
      alert("Please select your baseline activity level.");
      return;
    }
    setUserData((prev) => ({ ...prev, activityLevel }));
    nextStep();
  };

  return (
    <div className="step">
      <h2>What is your baseline activity level?</h2>
      <p>Not including workoutsâ€”we count that separately.</p>
      <div className="radio-group">
        <label>
          <input
            type="radio"
            name="activityLevel"
            value="Not Very Active (e.g., desk job)"
            checked={activityLevel === "Not Very Active (e.g., desk job)"}
            onChange={(e) => setActivityLevel(e.target.value)}
          />{" "}
          Not Very Active (e.g., desk job)
        </label>
        <br />
        <label>
          <input
            type="radio"
            name="activityLevel"
            value="Lightly Active (e.g., teacher)"
            checked={activityLevel === "Lightly Active (e.g., teacher)"}
            onChange={(e) => setActivityLevel(e.target.value)}
          />{" "}
          Lightly Active (e.g., teacher)
        </label>
        <br />
        <label>
          <input
            type="radio"
            name="activityLevel"
            value="Active (e.g., food server)"
            checked={activityLevel === "Active (e.g., food server)"}
            onChange={(e) => setActivityLevel(e.target.value)}
          />{" "}
          Active (e.g., food server)
        </label>
        <br />
        <label>
          <input
            type="radio"
            name="activityLevel"
            value="Very Active (e.g., bike messenger)"
            checked={activityLevel === "Very Active (e.g., bike messenger)"}
            onChange={(e) => setActivityLevel(e.target.value)}
          />{" "}
          Very Active (e.g., bike messenger)
        </label>
      </div>
      <div className="button-group">
        <button className="btn-secondary" onClick={prevStep}>
          Back
        </button>
        <button className="btn-primary" onClick={handleNext}>
          Next
        </button>
      </div>
    </div>
  );
}

export default StepFour;
