import React, { useState, useEffect } from "react";
import Calendar from "react-calendar";
import "react-calendar/dist/Calendar.css";
import "./Dashboard.css";

function Dashboard() {
  const [selectedDate, setSelectedDate] = useState(new Date());
  const [tasks, setTasks] = useState(() => JSON.parse(localStorage.getItem("tasks")) || []);
  const [newTask, setNewTask] = useState("");

  useEffect(() => {
    localStorage.setItem("tasks", JSON.stringify(tasks));
  }, [tasks]);

  const handleDateChange = (date) => {
    setSelectedDate(date);
  };

  const addTask = () => {
    if (newTask.trim() === "") return;
    setTasks([...tasks, { text: newTask, completed: false }]);
    setNewTask("");
  };

  const toggleTaskCompletion = (index) => {
    const updatedTasks = tasks.map((task, i) =>
      i === index ? { ...task, completed: !task.completed } : task
    );
    setTasks(updatedTasks);
  };

  const deleteTask = (index) => {
    const updatedTasks = tasks.filter((_, i) => i !== index);
    setTasks(updatedTasks);
  };

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h2>Welcome to Your Dashboard</h2>
        <p>Here's a quick overview of your day and insights.</p>
      </div>
      <div className="dashboard-content">
        {/* Left Column */}
        <div className="dashboard-left">
          <section className="dashboard-section tasks">
            <h3>Tasks for the Day</h3>
            <div className="task-input">
              <input
                type="text"
                placeholder="Add a new task..."
                value={newTask}
                onChange={(e) => setNewTask(e.target.value)}
              />
              <button onClick={addTask}>Add</button>
            </div>
            <ul className="task-list">
              {tasks.map((task, index) => (
                <li key={index} className={task.completed ? "completed" : ""}>
                  <span onClick={() => toggleTaskCompletion(index)}>{task.text}</span>
                  <button className="delete-task" onClick={() => deleteTask(index)}>
                    &times;
                  </button>
                </li>
              ))}
            </ul>
          </section>
          <section className="dashboard-section calendar">
            <h3>Your Calendar</h3>
            <Calendar
              onChange={handleDateChange}
              value={selectedDate}
              className="dashboard-calendar"
            />
            <p>
              Selected Date: <strong>{selectedDate.toDateString()}</strong>
            </p>
          </section>
        </div>
        {/* Right Column */}
        <div className="dashboard-right">
          <section className="dashboard-section food-waste">
            <h3>Global Food Waste</h3>
            <div className="chart-placeholder">
              <div className="pie-chart">[Pie Chart Placeholder]</div>
            </div>
          </section>
        </div>
      </div>
      <footer className="dashboard-footer">
        <p>&copy; 2024 Foodfolio. All rights reserved. Created by Aung Myat & Shreya Sharma.</p>
      </footer>
    </div>
  );
}

export default Dashboard;
